mapper.select:
SELECT * FROM concert

    AND concert_id = #{concertId}
    AND concert_name LIKE CONCAT('%', #{concertName}, '%')
    AND singer LIKE CONCAT('%', #{singer}, '%')
    AND start_time >= #{startTime}
    AND #{endTime}>=end_time
    AND venue LIKE CONCAT('%', #{venue}, '%')
    AND status = #{status}
    AND price = #{price}



        ORDER BY ${sortField} ${sortOrder}


        ORDER BY create_time DESC

mapper.update:
UPDATE concert
concert_name = #{concertName}, singer = #{singer}, start_time = #{startTime}, end_time = #{endTime}, venue = #{venue}, ticket_num = #{ticketNum}, remaining_tickets = #{remainingTickets}, status = #{status}, price = #{price},
WHERE concert_id = #{concertId}
