mapper.select:
SELECT * FROM `tb_order`

    AND order_id = #{orderId}
    AND user_id = #{userId}
    AND concert_id = #{concertId}
    AND order_status = #{orderStatus}
    AND create_time >= #{createTimeStart}
    AND create_time <= #{createTimeEnd}
    AND order_amount >= #{minAmount}
    AND order_amount <= #{maxAmount}



        ORDER BY ${sortField} ${sortOrder}


        ORDER BY create_time DESC

mapper.update:
UPDATE `tb_order`
user_id = #{userId}, concert_id = #{concertId}, ticket_num = #{ticketNum}, order_amount = #{orderAmount}, order_status = #{orderStatus}, create_time = #{createTime}, pay_time = #{payTime},
WHERE order_id = #{orderId}
